import { createBrandkit } from "@sitecore/stream-ui-core"

import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
} from "@/registry/new-york/ui/card"

import { BrandkitItem } from "./components/brandkit-item"

// Mock data for demo
const mockBrandkit = createBrandkit({
  id: "1",
  organizationId: "org-123",
  name: "Sitecore Brand",
  logo: "https://example.com/sitecore-logo.png",
  status: "published",
  description: "Sitecore brand assets",
  brandName: "Sitecore",
  companyName: "Sitecore",
  industry: "Technology",
})

export default function BrandkitItemPage() {
  return (
    <div className="container mx-auto p-6">
      <Card>
        <CardHeader>
          <CardTitle>Brandkit Item</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <BrandkitItem
              brandkit={mockBrandkit}
              onSelect={(brandkit) => console.log("Selected:", brandkit)}
            />
            <BrandkitItem
              brandkit={mockBrandkit}
              variant="outline"
              onSelect={(brandkit) => console.log("Selected:", brandkit)}
            />
            <BrandkitItem
              brandkit={mockBrandkit}
              variant="ghost"
              onSelect={(brandkit) => console.log("Selected:", brandkit)}
            />
          </div>
        </CardContent>
      </Card>
    </div>
  )
}

"use client"

import React from "react"
import { Brandkit } from "@sitecore/stream-ui-core"

import { cn, getDocumentProxyUrl } from "@/registry/new-york/lib/utils"
import {
  Avatar,
  AvatarFallback,
  AvatarImage,
} from "@/registry/new-york/ui/avatar"
import { Button } from "@/registry/new-york/ui/button"

export interface BrandkitItemProps {
  brandkit: Brandkit
  onSelect?: (brandkit: Brandkit) => void
  testId?: string
  variant?: "link" | "default" | "ghost" | "outline" | null | undefined
  className?: string
  showAvatar?: boolean
  children?: React.ReactNode
}

export function BrandkitItem({
  brandkit,
  onSelect,
  testId,
  className,
  variant = "ghost",
  showAvatar = true,
  children,
}: BrandkitItemProps): React.ReactNode {
  /* Events */
  const handleBrandkitOnSelect = (brandkit: Brandkit): void => {
    onSelect?.(brandkit)
  }

  return (
    <Button
      data-testid={testId}
      variant={variant}
      className={cn(
        "flex items-center justify-start rounded-md px-1 py-4",
        className
      )}
      onClick={() => handleBrandkitOnSelect(brandkit)}
      title={brandkit.name}
    >
      {showAvatar && (
        <Avatar className="h-9 w-9 border-2">
          <AvatarImage
            className="object-contain"
            src={getDocumentProxyUrl(brandkit?.logo)}
            alt={brandkit?.name}
          />
          <AvatarFallback className="flex h-full w-full items-center justify-center font-bold text-gray-500">
            {brandkit?.name.charAt(0).toUpperCase()}
          </AvatarFallback>
        </Avatar>
      )}
      <span className="overflow-hidden overflow-ellipsis whitespace-nowrap">
        {brandkit?.name}
      </span>
      {children}
    </Button>
  )
}
