import { createBrandkit } from "@sitecore/stream-ui-core"

import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
} from "@/registry/new-york/ui/card"

import { BrandkitList } from "./components/brandkit-list"

// Mock data for demo
const mockBrandkits = [
  createBrandkit({
    id: "1",
    organizationId: "org-123",
    name: "Sitecore Brand",
    logo: "https://example.com/sitecore-logo.png",
    status: "published",
    brandName: "Sitecore",
    companyName: "Sitecore",
    industry: "Technology",
  }),
  createBrandkit({
    id: "2",
    organizationId: "org-123",
    name: "ContentHub Brand",
    logo: "https://example.com/contenthub-logo.png",
    status: "published",
    brandName: "ContentHub",
    companyName: "Sitecore",
    industry: "Technology",
  }),
]

export default function BrandkitListPage() {
  return (
    <div className="container mx-auto p-6">
      <Card>
        <CardHeader>
          <CardTitle>Brandkit List</CardTitle>
        </CardHeader>
        <CardContent>
          <BrandkitList
            brandkits={mockBrandkits}
            onSelect={(brandkit) => console.log("Selected:", brandkit)}
            loading={false}
            placeholder="Search brandkits..."
          />
        </CardContent>
      </Card>
    </div>
  )
}

"use client"

import React, { useEffect, useState } from "react"
import { type Brandkit } from "@sitecore/stream-ui-core"

import { BrandkitItem } from "@/registry/new-york/blocks/brandkit-item/components/brandkit-item"
import { Spinner } from "@/registry/new-york/blocks/spinner/components/spinner"
import { cn } from "@/registry/new-york/lib/utils"
import { Input } from "@/registry/new-york/ui/input"
import { Separator } from "@/registry/new-york/ui/separator"

export interface BrandkitListProps {
  brandkits: Brandkit[]
  recentBrandkit?: Brandkit
  onSelect?: (brandkit: Brandkit) => void
  onSearch?: (query: string) => Brandkit[]
  loading?: boolean
  placeholder?: string
  className?: string
  emptyStateMessage?: string
  testId?: string
}

export function BrandkitList({
  brandkits,
  recentBrandkit,
  onSelect,
  onSearch,
  loading,
  placeholder = "Search",
  className,
  emptyStateMessage,
  testId,
}: BrandkitListProps): React.ReactNode {
  /* Hooks */
  const [filteredBrandkits, setFilteredBrandkits] = useState<Brandkit[]>([])
  const [searchQuery, setSearchQuery] = useState("")

  /* Events */
  const handleBrandkitOnSelect = (brandkit: Brandkit): void => {
    onSelect?.(brandkit)
  }

  const handleSearchOnChange = (query: string): void => {
    setSearchQuery(query)
    if (onSearch) {
      setFilteredBrandkits(onSearch(query))
      return
    }
    setFilteredBrandkits(
      brandkits.filter((brandkit) =>
        brandkit.name.toLowerCase().includes(query.toLowerCase())
      )
    )
  }

  useEffect(() => {
    if (brandkits) setFilteredBrandkits(brandkits)
  }, [brandkits])

  return (
    <section
      className={cn("flex flex-col gap-2", className)}
      data-testid={testId}
    >
      <div className="flex flex-col gap-2">
        <span className="flex items-center gap-2 font-semibold">
          Recent Brand
        </span>
        {recentBrandkit && (
          <BrandkitItem
            brandkit={recentBrandkit}
            onSelect={handleBrandkitOnSelect}
          />
        )}
        {loading && <div className="py-1">Loading brandkit...</div>}
        {recentBrandkit === undefined && !loading && (
          <div className="py-1">No recent brandkit</div>
        )}
        <Separator />
        <span className="my-4 flex items-center gap-2 font-semibold">
          All Brands {loading && <Spinner size={"xs"} />}
        </span>
        <Input
          type="search"
          placeholder={placeholder}
          className="rounded-lg"
          value={searchQuery}
          onChange={(e: { target: { value: string } }) =>
            handleSearchOnChange(e.target.value)
          }
        />
      </div>
      <div className="mt-2 flex max-h-96 min-h-48 flex-1 flex-col gap-1 overflow-y-auto">
        {filteredBrandkits?.map((brandkit: Brandkit) => (
          <BrandkitItem
            key={brandkit.id}
            brandkit={brandkit}
            onSelect={handleBrandkitOnSelect}
          />
        ))}

        {loading && (
          <div className="grid flex-1 place-items-center">
            <div className="flex flex-col items-center gap-6">
              <Spinner size="lg" />
              <span>Loading brandkits...</span>
            </div>
          </div>
        )}

        {!brandkits?.length && !loading && (
          <div className="grid flex-1 place-items-center">
            <div className="flex flex-col items-center gap-6">
              <span>{emptyStateMessage || "No brandkits found"}</span>
            </div>
          </div>
        )}
      </div>
    </section>
  )
}
