@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@tailwind base;
@tailwind components;
@tailwind utilities;

@import "./colors.css";
@import "./typography.css";
@import "./borderRadius.css";
@import "./breakpoints.css";
@import "./shadows.css";

:root {
  --radius: 0.625rem;
  /* Chakra semantic token mappings (light mode) */
  --background: var(--color-white); /* chakra-body-bg: white */
  --foreground: var(
    --color-blackAlpha-900
  ); /* chakra-body-text: blackAlpha.900 */
  --card: var(--color-white); /* chakra-card-bg: white */
  --card-foreground: var(
    --color-blackAlpha-900
  ); /* chakra-card-text: blackAlpha.900 */
  --popover: var(--color-white); /* chakra-popover-bg: white */
  --popover-foreground: var(
    --color-blackAlpha-900
  ); /* chakra-popover-text: blackAlpha.900 */
  --primary: var(--color-primary-500); /* primary-fg: primary.600 */
  --primary-foreground: var(--color-white); /* chakra-inverse-text: white */
  --primary-background: var(--color-primary-100);
  --secondary: var(--color-gray-50);
  --secondary-foreground: var(--color-blackAlpha-900);
  --muted: var(--color-gray-50); /* chakra-subtle-bg: gray.50 */
  --muted-foreground: var(
    --color-blackAlpha-500
  ); /* chakra-subtle-text: blackAlpha.500 */
  --accent: var(--color-gray-50);
  --accent-foreground: var(--color-blackAlpha-900);
  --destructive: var(--color-danger-500); /* danger-fg: danger.600 */
  --border: var(
    --color-blackAlpha-200
  ); /* chakra-border-color: blackAlpha.200 */
  --input: var(
    --color-blackAlpha-400
  ); /* chakra-placeholder-color: blackAlpha.400 */
  --ring: #6987f9;
  --chart-1: var(--color-purple-500);  /* 6e3fff */
  --chart-2: var(--color-orange-300);    /* 4a65e8 */
  --chart-3: var(--color-cyan-500);    /* 0873c8 */
  --chart-4: var(--color-red-400);    /* 007c85 */
  --chart-5: var(--color-green-500);   /* 007f66 */
  --sidebar: oklch(0.985 0 0);
  --sidebar-foreground: oklch(0.145 0 0);
  --sidebar-primary: oklch(0.205 0 0);
  --sidebar-primary-foreground: oklch(0.985 0 0);
  --sidebar-accent: oklch(0.97 0 0);
  --sidebar-accent-foreground: oklch(0.205 0 0);
  --sidebar-border: oklch(0.922 0 0);
  --sidebar-ring: oklch(0.708 0 0);
}

.dark {
  /* Chakra semantic token mappings (dark mode) */
  --background: var(--color-gray-800); /* chakra-body-bg: gray.800 */
  --foreground: var(--color-white); /* chakra-body-text: white */
  --card: var(--color-gray-800); /* chakra-card-bg: gray.800 */
  --card-foreground: var(--color-white); /* chakra-card-text: white */
  --popover: var(--color-gray-800); /* chakra-popover-bg: gray.800 */
  --popover-foreground: var(--color-white); /* chakra-popover-text: white */
  --primary: var(--color-primary-200); /* primary-fg: primary.200 */
  --primary-foreground: var(
    --color-blackAlpha-900
  ); /* chakra-inverse-text: blackAlpha.900 */
  --primary-background: var(--color-primary-100);
  --secondary: var(--color-gray-900); /* chakra-subtle-bg: gray.900 */
  --secondary-foreground: var(--color-white); /* chakra-subtle-text: white */
  --muted: var(--color-gray-900); /* chakra-subtle-bg: gray.900 */
  --muted-foreground: var(
    --color-whitealpha-600
  ); /* chakra-subtle-text: whiteAlpha.600 */
  --accent: var(--color-gray-900);
  --accent-foreground: var(--color-white); /* chakra-inverse-text: white */
  --destructive: var(--color-danger-200); /* danger-fg: danger.200 */
  --border: var(
    --color-whitealpha-200
  ); /* chakra-border-color: whiteAlpha.200 */
  --input: var(
    --color-whitealpha-500
  ); /* chakra-placeholder-color: whiteAlpha.500 */
  --ring: #6987f9;
  --input: oklch(1 0 0 / 15%);
  --ring: oklch(0.556 0 0);
  --chart-1: var(--color-purple-500);  /* 6e3fff */
  --chart-2: var(--color-blue-500);    /* 4a65e8 */
  --chart-3: var(--color-cyan-500);    /* 0873c8 */
  --chart-4: var(--color-teal-500);    /* 007c85 */
  --chart-5: var(--color-green-500);   /* 007f66 */
  --sidebar: oklch(0.205 0 0);
  --sidebar-foreground: oklch(0.985 0 0);
  --sidebar-primary: oklch(0.488 0.243 264.376);
  --sidebar-primary-foreground: oklch(0.985 0 0);
  --sidebar-accent: oklch(0.269 0 0);
  --sidebar-accent-foreground: oklch(0.985 0 0);
  --sidebar-border: oklch(1 0 0 / 10%);
  --sidebar-ring: oklch(0.439 0 0);
}

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-primary-background: var(--primary-background);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
  }
}

