import { TrendingUp } from "lucide-react";
import * as React from "react";
import { Label, Pie, PieChart } from "recharts";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import {
  type ChartConfig,
  ChartContainer,
  ChartTooltip,
  ChartTooltipContent,
} from "@/components/ui/chart";

export default function PieChartDemo() {
  return (
    <Card style="outline" className="w-4xl">
      <CardHeader className="items-center pb-0">
        <CardTitle>Pie Chart</CardTitle>
        <CardDescription>January - June 2024</CardDescription>
      </CardHeader>
      <CardContent className="flex-1 pb-0">
        <ChartContainer
        config={chartConfig}
        className="mx-auto aspect-square max-h-[250px]"
        >
        <PieChart
        role="img"
        aria-labelledby="chartTitle"
        tabIndex={0}
        >
        <title id="chartTitle">Pie chart showing browser usage distribution with total visitors</title>
        <ChartTooltip
        cursor={false}
        content={<ChartTooltipContent hideLabel />}
        />
        <Pie
        data={chartData}
        dataKey="visitors"
        nameKey="browser"
        innerRadius={60}
        strokeWidth={5}
        >
        <Label
        content={({ viewBox }) => {
        if (viewBox && "cx" in viewBox && "cy" in viewBox) {
        return (
        <text
        x={viewBox.cx}
        y={viewBox.cy}
        textAnchor="middle"
        dominantBaseline="middle"
        >
        <tspan
        x={viewBox.cx}
        y={viewBox.cy}
        className="fill-foreground font-bold text-3xl"
        >
        {totalVisitors.toLocaleString()}
      </tspan>
      <tspan
      x={viewBox.cx}
      y={(viewBox.cy || 0) + 24}
      className="fill-muted-foreground"
      >
      Visitors
    </tspan>
    </text>
    );
    }
    }}
    />
    </Pie>
    </PieChart>
    </ChartContainer>
    </CardContent>
    <CardFooter className="flex-col gap-2 text-sm">
      <div className="flex items-center gap-2 font-medium leading-none">
        Trending up by 5.2% this month <TrendingUp className="h-4 w-4" aria-hidden="true" />
      </div>
      <div className="text-muted-foreground leading-none">
        Showing total visitors for the last 6 months
      </div>
    </CardFooter>
    </Card>
  );
}
