"use client";

import * as React from "react";
import { CheckIcon, ChevronsUpDown } from "lucide-react";
import { cn } from "@/lib/utils";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
} from "@/components/ui/command";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { Icon } from "@/lib/icon";
import { mdiPlus } from "@mdi/js";

const users = [
  {
    id: "1",
    username: "ChristianHahn",
  },
  {
    id: "2",
    username: "ThomasKelly",
  },
  {
    id: "3",
    username: "FrankGrinaert",
  },
  {
    id: "4",
    username: "SpyridonMisichronis",
  },
  {
    id: "5",
    username: "LasithGunaratne",
  }
] as const;

export default function UserComboboxDemo() {
  const [open, setOpen] = React.useState(false);
  const [value, setValue] = React.useState(users[0].id);

  const selectedUser = React.useMemo(
    () => users.find((user) => user.id === value),
    [value]
  );

  return (
    <div className="p-1">
      <Popover open={open} onOpenChange={setOpen}>
        <PopoverTrigger asChild>
          <Button
            variant="outline"
            colorScheme="neutral"
            role="combobox"
            aria-expanded={open}
            aria-label="Select user"
            className={cn(
              "w-full justify-between px-2 md:max-w-[200px] rounded-md",
              open && "ring-primary ring-[2px]"
            )}
          >
            {selectedUser ? (
              <div className="flex items-center gap-2">
                <Avatar className="size-5">
                  <AvatarImage
                    src={`/${selectedUser.username}.png`}
                    alt={`${selectedUser.username} avatar`}
                  />
                  <AvatarFallback>{selectedUser.username[0]}</AvatarFallback>
                </Avatar>
                {selectedUser.username}
              </div>
            ) : (
              "Select user..."
            )}
            <ChevronsUpDown className="text-muted-foreground" />
          </Button>
        </PopoverTrigger>
        <PopoverContent className="w-(--radix-popover-trigger-width) p-0">
          <Command>
            <CommandInput placeholder="Search user..." />
            <CommandList className="max-h-[300px] pb-12 relative">
              <CommandEmpty>No user found.</CommandEmpty>
              <CommandGroup>
                {users.map((user) => (
                  <CommandItem
                    key={user.id}
                    value={user.id}
                    onSelect={(currentValue) => {
                      setValue(currentValue === value ? "" : currentValue);
                      setOpen(false);
                    }}
                  >
                    <Avatar className="size-5">
                      <AvatarImage
                        src={`https://github.com/${user.username}.pngg`}
                        alt={`${user.username} avatar`}
                      />
                      <AvatarFallback>{user.username[0]}</AvatarFallback>
                    </Avatar>
                    {user.username}
                    <CheckIcon
                      className={cn(
                        "ml-auto",
                        value === user.id ? "opacity-100" : "opacity-0"
                      )}
                    />
                  </CommandItem>
                ))}
              </CommandGroup>
            </CommandList>
            <div className="absolute bottom-0 left-0 right-0 border-x border-b bg-background p-2 rounded-b-md z-10">
              <Button variant="ghost" size="sm" colorScheme="primary" aria-label="Create new user">
                <Icon path={mdiPlus} size={0.9} className="" />
                Create user
              </Button>
            </div>
          </Command>
        </PopoverContent>
      </Popover>
    </div>
  );
