"use client";

import * as React from "react";
import { DndContext, DragEndEvent, UniqueIdentifier } from "@/components/ui/dnd-context";
import { DragOverlay } from "@/components/ui/drag-overlay";
import { Draggable } from "@/components/ui/draggable";
import { Droppable } from "@/components/ui/droppable";
import { Button } from "@/components/ui/button";
import { Move, CheckCircle2 } from "lucide-react";
import { cn } from "@/lib/utils";

export default function DefaultDemo() {
  const [container, setContainer] = React.useState<"source" | "drop-zone">("source");
  const [activeId, setActiveId] = React.useState<UniqueIdentifier | null>(null);

  const handleDragEnd = (event: DragEndEvent) => {
    const { over } = event;
    setActiveId(null);
    if (over) {
      setContainer(over.id === "drop-zone" ? "drop-zone" : "source");
    }
  };

  const draggableContent = (
    <Button className="w-full shadow-md" size="lg">
      <Move className="mr-2 h-4 w-4" />
      draggable
    </Button>
  );

  return (
    <DndContext onDragStart={({ active }) => setActiveId(active.id)} onDragEnd={handleDragEnd}>
      <div className="p-6 flex gap-6">
        <Droppable id="source" className="w-64 min-h-[120px] rounded-xl border-2 border-dashed p-4">
          {container === "source" ? (
            <Draggable id="draggable-button">{draggableContent}</Draggable>
          ) : (
            <p className="text-xs text-muted-foreground text-center py-4">Drop here to return</p>
          )}
        </Droppable>
        <Droppable id="drop-zone" className="w-64 min-h-[120px] rounded-xl border-2 border-dashed p-4 flex items-center justify-center">
          {container === "drop-zone" ? (
            <Draggable id="draggable-button">{draggableContent}</Draggable>
          ) : (
            <p className="text-sm text-muted-foreground">Drop here</p>
          )}
        </Droppable>
      </div>
      <DragOverlay>{activeId ? draggableContent : null}</DragOverlay>
    </DndContext>
  );
}

