Drag, Drop & Sort:
"use client";

import * as React from "react";
import {
  DndContext,
  DragEndEvent,
  DragOverEvent,
  DragStartEvent,
  SortableContainer,
  arrayMove,
  UniqueIdentifier,
  closestCorners,
} from "@/components/ui/dnd-context";
import { Draggable } from "@/components/ui/draggable";
import { Droppable } from "@/components/ui/droppable";
import { SortableItem } from "@/components/ui/sortable";
import { DragOverlay } from "@/components/ui/drag-overlay";
import { Card, CardContent } from "@/components/ui/card";
import { Star, Package } from "lucide-react";
import { cn } from "@/lib/utils";

interface CardItem {
  id: string;
  name: string;
}

const initialCards: CardItem[] = [
  { id: "card-1", name: "Design System" },
  { id: "card-2", name: "API Integration" },
  { id: "card-3", name: "User Testing" },
];

const cardColors = ["bg-info-bg", "bg-success-bg", "bg-warning-bg"];

function getCardColor(id: string) {
  const index = initialCards.findIndex((c) => c.id === id);
  return cardColors[index % cardColors.length];
}

function ItemCard({ item, index }: { item: CardItem; index?: number }) {
  return (
    <Card className={cn(getCardColor(item.id), "hover:shadow-sm")}>
      <CardContent className="p-2.5">
        <div className="flex items-center gap-2">
          {index !== undefined && (
            <span className="w-5 h-5 rounded-full bg-card flex items-center justify-center text-[10px] font-semibold">
              {index + 1}
            </span>
          )}
          <p className="font-medium text-xs">{item.name}</p>
        </div>
      </CardContent>
    </Card>
  );
}

export function DragDropSortableDemo() {
  const [sourceCards, setSourceCards] = React.useState<CardItem[]>(initialCards);
  const [favoriteCards, setFavoriteCards] = React.useState<CardItem[]>([]);
  const [activeId, setActiveId] = React.useState<UniqueIdentifier | null>(null);
  const [activeItem, setActiveItem] = React.useState<CardItem | null>(null);

  const handleDragStart = (event: DragStartEvent) => {
    setActiveId(event.active.id);
    const fromSource = sourceCards.find((c) => c.id === event.active.id);
    const fromFavorites = favoriteCards.find((c) => c.id === event.active.id);
    setActiveItem(fromSource || fromFavorites || null);
  };

  const handleDragEnd = (event: DragEndEvent) => {
    const { active, over } = event;
    setActiveId(null);
    setActiveItem(null);
    if (!over) return;

    const activeId = active.id as string;
    const overId = over.id as string;

    // Sorting within favorites
    const isActiveInFavorites = favoriteCards.some((c) => c.id === activeId);
    const isOverInFavorites = favoriteCards.some((c) => c.id === overId);
    if (isActiveInFavorites && isOverInFavorites && activeId !== overId) {
      setFavoriteCards((prev) => {
        const oldIndex = prev.findIndex((c) => c.id === activeId);
        const newIndex = prev.findIndex((c) => c.id === overId);
        return arrayMove(prev, oldIndex, newIndex);
      });
    }

    // Source to favorites
    const isFromSource = sourceCards.some((c) => c.id === activeId);
    if (isFromSource && overId === "favorites-drop") {
      const cardToMove = sourceCards.find((c) => c.id === activeId);
      if (cardToMove) {
        setSourceCards((prev) => prev.filter((c) => c.id !== activeId));
        setFavoriteCards((prev) => [...prev, cardToMove]);
      }
    }

    // Favorites to source
    const isFromFavorites = favoriteCards.some((c) => c.id === activeId);
    if (isFromFavorites && overId === "source-drop") {
      const cardToMove = favoriteCards.find((c) => c.id === activeId);
      if (cardToMove) {
        setFavoriteCards((prev) => prev.filter((c) => c.id !== activeId));
        setSourceCards((prev) => [...prev, cardToMove]);
      }
    }
  };

  const activeIndexInFavorites = activeItem ? favoriteCards.findIndex((c) => c.id === activeItem.id) : -1;

  return (
    <DndContext collisionDetection={closestCorners} onDragStart={handleDragStart} onDragEnd={handleDragEnd}>
      <div className="p-6 space-y-6">
        <div>
          <h3 className="text-2xl font-semibold">Drag, Drop & Sort</h3>
          <p className="text-sm text-muted-foreground mt-2">Drag cards between panels. Reorder favorites.</p>
        </div>
        <div className="flex gap-6">
          <Droppable id="source-drop" className="flex-1 p-4 rounded-xl border-2 border-dashed min-h-[200px]">
            {sourceCards.length > 0 ? (
              <div className="space-y-2">
                {sourceCards.map((card) => (
                  <Draggable key={card.id} id={card.id}>
                    <ItemCard item={card} />
                  </Draggable>
                ))}
              </div>
            ) : (
              <div className="flex items-center justify-center h-full">
                <p className="text-sm text-muted-foreground">Drag items back here</p>
              </div>
            )}
          </Droppable>
          <Droppable id="favorites-drop" className="flex-1 p-4 rounded-xl border-2 border-dashed min-h-[200px] bg-yellow-50/50">
            {favoriteCards.length > 0 ? (
              <SortableContainer items={favoriteCards.map((c) => c.id)} strategy="vertical">
                <div className="space-y-2">
                  {favoriteCards.map((card, index) => (
                    <SortableItem key={card.id} id={card.id}>
                      <ItemCard item={card} index={index} />
                    </SortableItem>
                  ))}
                </div>
              </SortableContainer>
            ) : (
              <div className="flex flex-col items-center justify-center h-full">
                <Star className="h-12 w-12 text-yellow-300 mb-3" />
                <p className="text-sm text-muted-foreground">Drop items here</p>
              </div>
            )}
          </Droppable>
        </div>
      </div>
      <DragOverlay>
        {activeItem ? (
          <Card className={cn(getCardColor(activeItem.id), "shadow-lg")}>
            <CardContent className="p-2.5">
              <div className="flex items-center gap-2">
                {activeIndexInFavorites >= 0 && (
                  <span className="w-5 h-5 rounded-full bg-card flex items-center justify-center text-[10px] font-semibold">
                    {activeIndexInFavorites + 1}
                  </span>
                )}
                <p className="font-medium text-xs">{activeItem.name}</p>
              </div>
            </CardContent>
          </Card>
        ) : null}
      </DragOverlay>
    </DndContext>
  );
}
