Dropdown Menu Checkboxes:
"use client";

import * as React from "react";
import {
  mdiAccountOutline,
  mdiCreditCardOutline,
  mdiCogOutline,
  mdiLogout,
} from "@mdi/js";
import { Icon } from "@/lib/icon";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuGroup,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

export default function DropdownMenuCheckboxesDemo() {
  const [showStatusBar, setShowStatusBar] = React.useState(true);
  const [showActivityBar, setShowActivityBar] = React.useState(false);
  const [showPanel, setShowPanel] = React.useState(false);

  return (
    <DropdownMenu id="dropdown-menu-checkboxes">
      <DropdownMenuTrigger id="dropdown-menu-checkboxes-trigger" asChild>
        <Button variant="outline" colorScheme="neutral">
          Checkboxes
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent id="dropdown-menu-checkboxes-content" align="start" className="w-56">
        <DropdownMenuGroup id="dropdown-menu-checkboxes-group-account">
          <DropdownMenuLabel id="dropdown-menu-checkboxes-label-account">Account</DropdownMenuLabel>
          <DropdownMenuItem id="dropdown-menu-checkboxes-item-profile">
            <Icon path={mdiAccountOutline} size={1.5} /> Profile
          </DropdownMenuItem>
          <DropdownMenuItem id="dropdown-menu-checkboxes-item-billing">
            <Icon path={mdiCreditCardOutline} size={1.5} /> Billing
          </DropdownMenuItem>
          <DropdownMenuItem id="dropdown-menu-checkboxes-item-settings">
            <Icon path={mdiCogOutline} size={1.5} /> Settings
          </DropdownMenuItem>
        </DropdownMenuGroup>
        <DropdownMenuSeparator id="dropdown-menu-checkboxes-separator-1" />
        <DropdownMenuGroup id="dropdown-menu-checkboxes-group-appearance">
          <DropdownMenuLabel id="dropdown-menu-checkboxes-label-appearance">Appearance</DropdownMenuLabel>
          <DropdownMenuCheckboxItem
            id="dropdown-menu-checkboxes-item-statusbar"
            checked={showStatusBar}
            onCheckedChange={setShowStatusBar}
          >
            Status Bar
          </DropdownMenuCheckboxItem>
          <DropdownMenuCheckboxItem
            id="dropdown-menu-checkboxes-item-activitybar"
            checked={showActivityBar}
            onCheckedChange={setShowActivityBar}
            disabled
          >
            Activity Bar
          </DropdownMenuCheckboxItem>
          <DropdownMenuCheckboxItem
            id="dropdown-menu-checkboxes-item-panel"
            checked={showPanel}
            onCheckedChange={setShowPanel}
          >
            Panel
          </DropdownMenuCheckboxItem>
        </DropdownMenuGroup>
        <DropdownMenuSeparator id="dropdown-menu-checkboxes-separator-2" />
        <DropdownMenuGroup id="dropdown-menu-checkboxes-group-signout">
          <DropdownMenuItem id="dropdown-menu-checkboxes-item-signout">
            <Icon path={mdiLogout} size={1.5} /> Sign Out
          </DropdownMenuItem>
        </DropdownMenuGroup>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

Dropdown Menu Radio Group Demo:
"use client";

import * as React from "react";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuGroup,
  DropdownMenuLabel,
  DropdownMenuRadioGroup,
  DropdownMenuRadioItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

export default function DropdownMenuRadioGroupDemoDemo() {
  const [position, setPosition] = React.useState("bottom");

  return (
    <DropdownMenu id="dropdown-menu-radio-group">
      <DropdownMenuTrigger id="dropdown-menu-radio-group-trigger" asChild>
        <Button variant="outline" colorScheme="neutral">
          Radio Group
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent id="dropdown-menu-radio-group-content" align="start" className="w-56">
        <DropdownMenuLabel id="dropdown-menu-radio-group-label" inset>Panel Position</DropdownMenuLabel>
        <DropdownMenuGroup id="dropdown-menu-radio-group-group">
          <DropdownMenuRadioGroup id="dropdown-menu-radio-group-radios" value={position} onValueChange={setPosition}>
            <DropdownMenuRadioItem id="dropdown-menu-radio-group-item-top" value="top">Top</DropdownMenuRadioItem>
            <DropdownMenuRadioItem id="dropdown-menu-radio-group-item-bottom" value="bottom">Bottom</DropdownMenuRadioItem>
            <DropdownMenuRadioItem id="dropdown-menu-radio-group-item-right" value="right" disabled>
              Right
            </DropdownMenuRadioItem>
          </DropdownMenuRadioGroup>
        </DropdownMenuGroup>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

