With Controls:
import {
  Editable,
  EditablePreview,
  EditableInput,
  EditableControl,
  EditableEditTrigger,
  EditableCancelTrigger,
  EditableSubmitTrigger,
} from "@/components/ui/editable";
import { Icon } from "@/lib/icon";
import { mdiPencil, mdiCheck, mdiClose } from "@mdi/js";

export default function WithControlsDemo() {
  return (
    <div className="w-80">
      <Editable
        defaultValue="Edit me with buttons"
        placeholder="Enter text..."
        isPreviewFocusable={false}
        submitOnBlur={false}
      >
        <div className="flex items-center gap-2">
          <EditablePreview className="flex-1" />
          <EditableInput className="flex-1" />
          <EditableControl>
            <EditableEditTrigger>
              <Icon path={mdiPencil} size="sm" />
            </EditableEditTrigger>
            <EditableSubmitTrigger>
              <Icon path={mdiCheck} size="sm" />
            </EditableSubmitTrigger>
            <EditableCancelTrigger>
              <Icon path={mdiClose} size="sm" />
            </EditableCancelTrigger>
          </EditableControl>
        </div>
      </Editable>
    </div>
  );
}

Controlled:
"use client";

import { useState } from "react";
import {
  Editable,
  EditablePreview,
  EditableInput,
} from "@/components/ui/editable";

export default function ControlledDemo() {
  const [value, setValue] = useState("Hello World");

  return (
    <div className="flex flex-col gap-4">
      <Editable
        value={value}
        onValueChange={setValue}
        placeholder="Enter text..."
      >
        <EditablePreview />
        <EditableInput />
      </Editable>
      <p className="text-sm text-muted-foreground ps-2">
        Current value: <span className="font-medium text-foreground">{value}</span>
      </p>
    </div>
  );
}

Store:
"use client";

import {
  EditableRootProvider,
  EditablePreview,
  EditableInput,
  useEditable,
} from "@/components/ui/editable";

export default function StoreDemo() {
  const editable = useEditable({
    defaultValue: "Click to edit",
  });

  return (
    <div className="flex flex-col items-start gap-4">
      <EditableRootProvider value={editable}>
        <EditablePreview />
        <EditableInput />
      </EditableRootProvider>
      <code className="text-xs px-2 py-1 bg-muted rounded font-mono">
        {editable.editing ? "editing" : "not editing"}
      </code>
    </div>
  );
}
